function s(){document.querySelectorAll(".p-faq__content-item").forEach(t=>{const a=t.querySelector(".p-faq__content-item-title"),e=t.querySelector(".p-faq__content-item-title-icon");!a||!e||(a.addEventListener("click",n=>{(n.target===e||e.contains(n.target))&&n.stopPropagation(),t.classList.contains("p-faq__content-item--open")?(t.classList.remove("p-faq__content-item--open"),e.setAttribute("aria-label","開く")):(t.classList.add("p-faq__content-item--open"),e.setAttribute("aria-label","閉じる"))}),e.addEventListener("click",n=>{n.stopPropagation(),t.classList.contains("p-faq__content-item--open")?(t.classList.remove("p-faq__content-item--open"),e.setAttribute("aria-label","開く")):(t.classList.add("p-faq__content-item--open"),e.setAttribute("aria-label","閉じる"))}))})}document.addEventListener("DOMContentLoaded",function(){s()});
