function n(){document.querySelectorAll(".p-join__item").forEach(e=>{const t=e.querySelector(".p-join__item-header");t&&t.addEventListener("click",()=>{e.classList.contains("p-join__item--open")?(e.classList.remove("p-join__item--open"),t.setAttribute("aria-expanded","false")):(e.classList.add("p-join__item--open"),t.setAttribute("aria-expanded","true"))})})}document.addEventListener("DOMContentLoaded",n);
